-- * Funções principais da Tarefa 3.

-- | Comprime um 'Estado' para formato textual.
--
-- __NB:__ A função 'show' representa um 'Estado' num formato textual facilmente legível mas extenso.
--
-- __NB:__ Uma boa solução deve representar o 'Estado' dado no mínimo número de caracteres possível.
comprime :: Estado -> String
comprime (Estado m j d) = comprimeMapa m  ++ comprimeJogadores j ++ comprimeDisparos d

-- | Comprime Mapa numa 'String'.
comprimeMapa :: Mapa -> String
comprimeMapa mapa = show coluna ++ ":" ++ comprimeLinhaMapa (concatMap meioLista meioMapa) -- Mostra o número de colunas coluna seguido de ":" seguido do 'Mapa' concatenado sem todas as bordas.
  where meioMapa = meioLista mapa --  Retira as 'Parede Indestrutivel' da primeira e última linha.
        coluna = length (head mapa) -2 --  Dá o número de colunas do 'Mapa' sem as bordas.

-- | Pega numa [Peca] comprime-a numa 'String'.
comprimeLinhaMapa :: [Peca] -> String
comprimeLinhaMapa [] = [] -- Quando não existe nenhuma Peca devolve o vazio.
comprimeLinhaMapa (p:ps)
   | n >= 3 = show n ++ pecMini ++ comprimeLinhaMapa (drop (n-1) ps) -- Se houverem 3 ou mais Peca iguais seguidas resulta em "nl" onde n é o número de vezes que a Peca ocorre e l é a String da Peca dada por pecMini.
   | otherwise = pecMini ++ comprimeLinhaMapa ps -- Se não escreve a 'Peca' na forma de 'String' dada por 'pecMini'.
    where n = contadorElementos p ps -- Conta quantas vezes a mesma 'Peca' aparece seguida.
          pecMini = comprimePeca p --  Comprime 'Peca' na respetiva 'String'.

-- | Tranforma 'Peca's em 'Char'.
comprimePeca :: Peca -> String
comprimePeca Vazia = "o" -- Transforma uma 'Peca' 'Vazia' em "o".
comprimePeca (Bloco Indestrutivel) = "x" -- Transforma uma 'Peca' 'Bloco Indestrutivel' em "x".
comprimePeca (Bloco Destrutivel) = "z" -- Transforma uma 'Peca' 'Bloco Destrutivel' em "z".

-- | Conta o número de elementos iguais seguidos.
contadorElementos :: Eq a => a -> [a] -> Int
contadorElementos _ [] = 1 -- Um elemnto mais uma lista 'Vazia' dá 1.
contadorElementos a (x:xs) -- Para um elemento a comparar com uma lista.
   | a == x = 1 + contadorElementos a xs -- Quando o elemnto é igual ao primeiro elemento da lista soma-lhe 1.
   | otherwise = 1 -- Se não for igual retorna 1, ou seja não existe nenhum igual a ele.

-- | Dá o 'Mapa' sem as bordas.
meioLista :: [a] -> [a]
meioLista [] = [] -- Numa lista 'Vazia' dá uma lista 'Vazia'.
meioLista xs = tail (init xs) -- Numa lista não 'Vazia' retira-lhe o primeiro e o último elemento.

-- | Transforma a lista de 'Disparo' numa 'String' mais compacta.
comprimeDisparos :: [Disparo] -> String
comprimeDisparos  = concatMap comprimeDisparo

-- | Transforma um 'Disparo' numa 'String'.
comprimeDisparo :: Disparo -> String
comprimeDisparo (DisparoLaser j (x,y) d) = "l" ++ show j ++ "|" ++ show x ++ "," ++ show y ++ show d
comprimeDisparo (DisparoChoque j t) = "s" ++ show j ++ "|" ++ show t
comprimeDisparo (DisparoCanhao j (x,y) d) = "r"  ++ show j ++ "|" ++ show x ++ "," ++ show y ++ show d

-- | Transforma a lista de 'Jogador' numa 'String' mais compacta.
comprimeJogadores :: [Jogador] -> String
comprimeJogadores = concatMap comprimeJogador

-- | Transforma um 'Jogador' numa 'String'.
comprimeJogador :: Jogador -> String
comprimeJogador (Jogador (x,y) d v l c) = "j" ++ show x ++ "," ++ show y ++ show d ++ show v ++ "|" ++ show l ++ "|" ++ show c

-- | Descomprime um 'Estado' no formato textual utilizado pela função 'comprime'.
--
-- __NB:__ A função 'comprime' é válida de for possível recuperar o 'Estado' utilizando a função 'descomprime', i.e.:
--
-- prop> descomprime . comprime = id
--
-- __NB:__ Esta propriedade é particularmente válida para a solução pré-definida:
--
-- prop> read . show = id
descomprime :: String -> Estado
descomprime s
   |'j' `elem` s && 's'  `elem` s || 'r'  `elem` s || 'l'  `elem` s = Estado m j d
   |'j' `notElem` s && 's'  `elem` s || 'r'  `elem` s || 'l'  `elem` s = Estado {mapaEstado = m, jogadoresEstado = [], disparosEstado = d}
   |'j' `elem` s && 's'  `notElem` s && 'r'  `notElem` s && 'l'  `notElem` s = Estado {mapaEstado = m, jogadoresEstado = j, disparosEstado = []}
   |'j' `notElem` s && 's'  `notElem` s && 'r'  `notElem` s && 'l'  `notElem` s = Estado {mapaEstado = m, jogadoresEstado = [], disparosEstado = []}
    where m = descomprimeMapa s
          j = descomprimeJogadores (stringParaListaString (getJogadores (getStringSemMapa  (getTabuleiro s))))
          d = descomprimeDisparos (stringParaListaString (getDisparos (getStringSemMapa  (getTabuleiro s))))

-- | Descomprime uma 'String' na lista de 'Jogador' inicial.
descompJog :: String -> [Jogador]
descompJog s = descomprimeJogadores (stringParaListaString (getJogadores (getStringSemMapa  (getTabuleiro s))))

-- | Pega na 'String' comprimida e tira-lhe o número de colunas e o ':'.
getTabuleiro :: String -> String
getTabuleiro (':':lista) = lista -- Quando o primeiro elemnto da String é ':' dá a restante String.
getTabuleiro (l:ls) = getTabuleiro ls -- Caso contrário vai-lhe retirando os elementos até enocontrar ':'.

