-- Descomprimir o Mapa

-- | Descomprime uma 'String' para o 'Mapa' original.
descomprimeMapa :: String -> Mapa
descomprimeMapa s =  mapaComBordas (listaPecasParaMapa (descomprimeStringParaPeca (numeroCharParaString (getMapa (getTabuleiro s)))) (tiraNumeros s))

-- | Pega na 'String' e fica com a parte que corresponde a um 'Mapa'.
getMapa :: String -> String
getMapa ('j':lista) = [] -- Quando na lista aparece j retorna [] uma vez que significa que está a passar para a parte do ['Jogador'] na 'String'.
getMapa ('l':lista) = [] -- Quando na lista aparece l retorna [] uma vez que significa que está a passar para a parte do ['Jogador'] na 'String'.
getMapa ('r':lista) = [] -- Quando na lista aparece r retorna [] uma vez que significa que está a passar para a parte do ['Jogador'] na 'String'.
getMapa ('s':lista) = [] -- Quando na lista aparece s retorna [] uma vez que significa que está a passar para a parte do ['Jogador'] na 'String'.
getMapa (x:xs) = x : getMapa xs -- Quando não aparece vai gerando a 'String' até a j,l,r ou s.
getMapa [] = []


-- | Tranforma 'Char' numa 'Peca'.
descomprimePeca :: Char -> Peca
descomprimePeca 'o' = Vazia -- Tranforma o numa 'Peca' 'Vazia'.
descomprimePeca 'x' = Bloco Indestrutivel -- Tranforma x numa 'Peca' 'Bloco Indestrutivel'.
descomprimePeca 'z' = Bloco Destrutivel -- Tranforma z numa 'Peca' 'Bloco Destrutivel'.

-- | Tranforma o 'Mapa' descomprimido, por exemplo "3o4z" fica "ooozzzz".
numeroCharParaString :: String -> String
numeroCharParaString [] = []
numeroCharParaString (x:xs)
   | isDigit x =  m ++ numeroCharParaString (drop (contaDigitos (x:xs)) (x:xs))
   | otherwise = head (drop (contaDigitos (x:xs)) (x:xs)) : numeroCharParaString (tail (drop (contaDigitos (x:xs)) (x:xs)))
   where m = multiplicaChar (head (drop (contaDigitos (x:xs)) (x:xs))) (tiraNumeros (x:xs))

-- | Conta o número de digitos seguidos inicialmente na 'String'.
contaDigitos :: String -> Int
contaDigitos string = length (takeWhile isDigit string)

-- | Pega num 'Char' e tranforma-o numa 'String' de n 'Char' iguais ao inicial.
multiplicaChar :: Char -> Int -> String
multiplicaChar c 1 = []
multiplicaChar c n = c : multiplicaChar c (n-1)

-- | Dá os números que estão no início de uma 'String'.
tiraNumeros :: String -> Int
tiraNumeros string = read (takeWhile isDigit string) :: Int

-- | Dado Uma 'String' tranforma em ['Peca'] exemplo : "oooozzxxxzz" -> [Vazia,Vazia,Vazia,Vazia,Bloco Destrutivel,Bloco Destrutivel,Bloco Indestrutivel,Bloco Indestrutivel,Bloco Indestrutivel,Bloco Destrutivel,Bloco Destrutivel].
descomprimeStringParaPeca :: String -> [Peca]
descomprimeStringParaPeca = map descomprimePeca

-- | Pega num 'Mapa' e faz-lhe a borda inferior e superior.
mapaComBordas :: Mapa -> Mapa
mapaComBordas (m:ms) = [fazBorda (length m)] ++ (m:ms) ++ [fazBorda (length m)]

-- | Pega numa ['Peca'] e tranforma-a num 'Mapa' cortando ['Peca'] em n ['Peca'].
listaPecasParaMapa :: [Peca] -> Int -> Mapa
listaPecasParaMapa [] _ = []
listaPecasParaMapa p n = (Bloco Indestrutivel : listaPecasParaLinha p n ++ [Bloco Indestrutivel]) : listaPecasParaMapa (drop n p) n

-- | Pega numa lista de 'Peca' e parte-a num determinado número, exemplo [Vazia,Vazia,Vazia] -> 2 -> [Vazia,Vazia].
listaPecasParaLinha :: [Peca] -> Int -> [Peca]
listaPecasParaLinha [] _ = []
listaPecasParaLinha (x:xs) 0 = []
listaPecasParaLinha (x:xs) n = x : listaPecasParaLinha xs (n-1)

