-- Descomprime Jogador

-- | Pega na 'String' e fica com a parte que corresponde aos 'Jogador'es.
getJogadores :: String -> String
getJogadores s = getStringSemDisp (getStringSemMapa s)

-- | Pega na 'String' e retira os 'Disparo's.
getStringSemDisp :: String -> String
getStringSemDisp ('l':lista) = []
getStringSemDisp ('s':lista) = []
getStringSemDisp ('r':lista) = []
getStringSemDisp (x:xs) = x : getStringSemDisp xs
getStringSemDisp [] = []

-- | Pega na 'String' e retira o 'Mapa'.
getStringSemMapa :: String -> String
getStringSemMapa ('j': lista) = 'j': lista
getStringSemMapa (x:xs) = getStringSemMapa xs

-- | Pega numa 'String' e separa-a em listas de 'String' apenas com um 'Char' ou com um núemro, incluindo núemros de multiplos algarismos.
stringParaListaString :: String -> [String]
stringParaListaString [] = []
stringParaListaString (x:xs)
   | x == '|' = stringParaListaString xs
   | isDigit x = takeWhile isDigit (x:xs) : stringParaListaString (dropWhile isDigit (x:xs))
   | otherwise = [x] : stringParaListaString xs

-- | Dá drop a um 'Jogador'.
dropJogador :: [String] -> [String]
dropJogador (x:xs) = dropWhile ("j">) xs

-- | Dá take a um 'Jogador'.
takeJogador :: [String] -> [String]
takeJogador (x:xs) = x : takeWhile ("j">) xs

-- | Transforma lista de 'String's numa lista de 'Jogador'es.
descomprimeJogadores :: [String] -> [Jogador]
descomprimeJogadores [] = []
descomprimeJogadores (x:xs) = descomprimeJogador (takeJogador (x:xs)) : descomprimeJogadores (dropJogador (x:xs))

-- | Tranforma lista de 'String's num 'Jogador'.
descomprimeJogador :: [String] -> Jogador
descomprimeJogador ("j":x:",":y:d:v:l:c:string)
   = Jogador (stringParaPosicao ("j":x:",":y:d:v:l:c:string)) (stringParaDirecao ("j":x:",":y:d:v:l:c:string)) (read v :: Int) (read l :: Int) (read c :: Int)

-- | Pega numa lista de 'String' e retira dela uma 'Posicao' nela contida.
stringParaPosicao :: [String] -> (Int,Int)
stringParaPosicao ("j":x:",":y:d:v:l:c:strings) = (read x :: Int, read y :: Int)
stringParaPosicao ("l":j:x:",":y:d:string) = (read x :: Int, read y :: Int)
stringParaPosicao ("r":j:x:",":y:d:string) = (read x :: Int, read y :: Int)

-- | Pega numa lista de 'String' e retira dela uma 'Direcao' nela contida.
stringParaDirecao :: [String] -> Direcao
stringParaDirecao ("j":x:",":y:d:v:l:c:strings) = read d :: Direcao
stringParaDirecao ("l":j:x:",":y:d:string) = read d :: Direcao
stringParaDirecao ("r":j:x:",":y:d:string) = read d :: Direcao

-- | Passa uma 'String' de números para um 'Int'.
stringParaNumeros :: String -> Int
stringParaNumeros s = read s :: Int

