-- Descomprimir Disparos

-- | Pega na 'String' e fica com a parte que corresponde aos 'Disparo'es.
getDisparos :: String -> String
getDisparos ('l':lista) = 'l':lista
getDisparos ('s':lista) = 's':lista
getDisparos ('r':lista) = 'r':lista
getDisparos (x:xs) = getDisparos xs

-- | Transforma lista de 'String's numa lista de 'Disparo'es.
descomprimeDisparos :: [String] -> [Disparo]
descomprimeDisparos [] = []
descomprimeDisparos (x:xs) = descomprimeDisparo (x:xs) : descomprimeDisparos (dropDisparo (x:xs))

-- | Tranforma lista de 'String's num 'Disparo'.
descomprimeDisparo :: [String] -> Disparo
descomprimeDisparo ("l":j:x:",":y:d:string)
   = DisparoLaser (read j :: Int) (stringParaPosicao ("l":j:x:",":y:d:string)) (stringParaDirecao ("l":j:x:",":y:d:string))
descomprimeDisparo ("r":j:x:",":y:d:string)
   = DisparoCanhao (read j :: Int) (stringParaPosicao ("r":j:x:",":y:d:string)) (stringParaDirecao ("r":j:x:",":y:d:string))
descomprimeDisparo ("s":j:t:string)
   = DisparoChoque (read j :: Int) (read t :: Int)

-- | Dá drop a um 'Disparo'.
dropDisparo :: [String] -> [String]
dropDisparo (x:xs) = dropWhile ("a">) xs

-- | Dá take a um 'Disparo'.
takeDisparo :: [String] -> [String]
takeDisparo (x:xs) = dropWhile ("a">) xs